/* eslint-disable react/react-in-jsx-scope */
import eact,{ useContext } from "react";
import { useState } from "react";
import { QuestionContext } from "../store/question-Context";

const QuestionSection = () => {
  const questions = useContext(QuestionContext).questions;
  const endQuizHandler = useContext(QuestionContext).endQuizHandler;
  const setScore = useContext(QuestionContext).addScore;

  const handleAnswerOptionClick = (isCorrect) => isCorrect && setScore();

  const [currentQuestion, setCurrentQuestion] = useState(0);

  const nextQuestion = currentQuestion + 1;
  if (nextQuestion < questions.length) {
    setCurrentQuestion(nextQuestion);
  } else {
    endQuizHandler(true);
  }

  return (
    <>
      <div className="question-section">
        <div className="question-count">
          <span>Question {currentQuestion + 1}</span>/{questions.length}
        </div>
        <div className="question-text">
          {questions[currentQuestion].questionText}
        </div>
      </div>

      <div className="answer-section">
        {questions[currentQuestion].answerOptions.map((answerOption) => (
          <button
            onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}
          >
            {answerOption.answerText}
          </button>
        ))}
      </div>
    </>
  );
};

export default QuestionSection;
