import React from "react";

const QuestionSection = (props) => {
  const indexQuestion = 0;

  const { questionText, answerOptions } = props.questions[indexQuestion];
  console.log(questionText);


  return (
    <>
      <div className="question-section">
        <div className="question-count">
          <span>Question {indexQuestion}</span>/{props.questions.length}
        </div>
        <div className="question-text">
          {props.questions[indexQuestion].questionText}
        </div>
      </div>
      <div className="answer-section">
        {props.questions[indexQuestion].answerOptions.map((option) => (
          <button key={option.answerText}>{option.answerText}</button>
        ))}
      </div>
    </>
  );
};

export default QuestionSection;
