import React from "react";
import { useState } from "react";

const QuestionSection = (props) => {
  const [indexQuestion, setIndexQuestion] = useState(0);

  const { questionText, answerOptions } = props.questions[indexQuestion];

  const answerHandler = (correct) => {
    correct && props.setScore((p) => p + 1);
    setIndexQuestion((p) => p + 1);
  };

  return (
    <>
      <div className="question-section">
        <div className="question-count">
          <span>Question {indexQuestion} +1</span>/{props.questions.length}
        </div>
        <div className="question-text">{questionText}</div>
      </div>
      <div className="answer-section">
        {answerOptions.map((option) => (
          <button
            key={option.answerText}
            onClick={() => answerHandler(option.isCorrect)}
          >
            {option.answerText}
          </button>
        ))}
      </div>
    </>
  );
};

export default QuestionSection;
