import React from "react";
import { useState, useContext } from "react";
import { QuizContext } from "../store/quizContext";

const QuestionSection = () => {
  const questions = useContext(QuizContext).questions;
  const score = useContext(QuizContext).score;
  const setScore = useContext(QuizContext).setScore;
  const endQuiz = useContext(QuizContext).endQuiz;
  const setEndQuiz = useContext(QuizContext).setEndQuiz;

  const [indexQuestion, setIndexQuestion] = useState(0);

  if (indexQuestion === questions.length) {
    setEndQuiz();
    return <></>;
  }

  const { questionText, answerOptions } = questions[indexQuestion];

  const answerHandler = (correct) => {
    correct && setScore();
    setIndexQuestion((p) => p + 1);
  };

  return (
    <>
      <div className="question-section">
        <div className="question-count">
          <span>Question {indexQuestion + 1}</span>/{questions.length}
        </div>
        <div className="question-text">{questionText}</div>
      </div>
      <div className="answer-section">
        {answerOptions.map((option) => (
          <button
            key={option.answerText}
            onClick={() => answerHandler(option.isCorrect)}
          >
            {option.answerText}
          </button>
        ))}
      </div>
    </>
  );
};

export default QuestionSection;
